INSERT INTO `wp_posts` VALUES("131", "1", "2017-06-12 09:26:07", "2017-06-12 06:26:07", "<div class=\"container-fluid \">\n                <div class=\"ddl-full-width-row row  ddl-frontend-editor-row ddl-frontend-editor-editable\"   data-id=\'32\' data-name=\'Row 1\' data-type=\'row\' data-layout_slug=\'\' data-kind=\'Row\' >\n                <div class=\"col-sm-12 ddl-frontend-editor-cell ddl-frontend-editor-editable js-ddl-frontend-editor-cell\" data-id=\'s8\' data-name=\'Visual Editor\' data-type=\'cell-text\' data-layout_slug=\'\' data-kind=\'Cell\'><p class=\"text-center lead\">To find an action that was implemented in another RIS3 action plan consult the list of actions below or use the search form</p></div></div></div>                <div class=\"container-fluid \">\n                <div class=\"ddl-full-width-row row  ddl-frontend-editor-row ddl-frontend-editor-editable\"   data-id=\'130\' data-name=\'Row 2\' data-type=\'row\' data-layout_slug=\'\' data-kind=\'Row\' >\n                <div class=\"col-sm-12 ddl-frontend-editor-cell ddl-frontend-editor-editable js-ddl-frontend-editor-cell\" data-id=\'s106\' data-name=\'Action Search\' data-type=\'views-content-grid-cell\' data-layout_slug=\'\' data-kind=\'Cell\'>[wpv-view name=\"view-1\"]</div></div></div>", "Find a Relevant Action", "", "inherit", "closed", "closed", "", "116-revision-v1", "", "", "2017-06-12 09:26:07", "2017-06-12 06:26:07", "", "116", "http://actionplan.s3platform.eu/2017/06/12/116-revision-v1/", "0", "revision", "", "0");
INSERT INTO `wp_posts` VALUES("201", "3", "2017-06-15 13:54:18", "2017-06-15 10:54:18", "<em>Action Plan Co-Design</em> is an application that permits regional authorities publish their RIS3 Action Plan in order to receive feedback and ideas from the public. The overall objective of this application is to provide a framework that facilitates citizens’ involvement in the design of the RIS3 Action Plan, so that it is better adjusted to their needs and priorities. The co-design of the RIS3 Action Plan can significantly contribute to maximize the successful implementation of the overall RIS3 strategy.\r\n\r\n<b>- Step 1. Publish the RIS3 Action Plan</b>: The RIS3 Action Plan is a set of actions and each action is defined by certain characteristics including<i>: (i) region and country, (ii) thematic objective or challenge addressed, (iii) investment priority under which the action is placed, (iv) short description of the action, (v) delivery mechanism(s), (vi) target groups/beneficiaries, (vii) actors involved, (viii) measurable targets, (ix) timeframes, (x) funding source(s) and (xii) budget.</i>\r\n\r\n<b>- Step 2. Receive feedback from citizens: </b>Citizens are able to express their opinion regarding each action of the RIS3 plan and its specific details through commenting, voting and suggesting modifications.\r\n\r\n<b>- Step 3. Update the RIS3 Action Plan according to citizens\' contribution: </b>After receiving feedback from users, regional authorities can update the Action Plan in order to meet users\' needs and priorities and achieve the desirable outcomes.\r\n<p class=\"text-center\"><a class=\"btn btn-primary btn-lg\" href=\"wp-admin/\"><i class=\"fa fa-refresh\"></i> Setup a Co-design Process</a>  <a class=\"btn btn-success btn-lg\" href=\"co-design-your-regions-action-plan/\"><i class=\"fa fa-comments\"></i> Participate to a Co-design Process</a></p>", "About the Action Plan Co-design Application", "", "inherit", "closed", "closed", "", "21-revision-v1", "", "", "2017-06-15 13:54:18", "2017-06-15 10:54:18", "", "21", "http://actionplan.s3platform.eu/2017/06/15/21-revision-v1/", "0", "revision", "", "0");
